	function date_compare($element1, $element2)
	{
		$datetime1 = strtotime($element1['timestamp']);
		$datetime2 = strtotime($element2['timestamp']);
		return $datetime1 - $datetime2;
	}

}
function turnUrlIntoHyperlink($string) {
	$reg_exUrl = "/(?i)\b((?:https?:\/\/|www\d{0,3}[.]|[a-z0-9.\-]+[.][a-z]{2,4}\/)(?:[^\s()<>]+|\(([^\s()<>]+|(\([^\s()<>]+\)))\))+(?:\(([^\s()<>]+|(\([^\s()<>]+\)))\)|[^\s`!()\[\]{};:'\".,<>?«»“”‘’]))/";
	$imgExts = array("gif", "jpg", "jpeg", "png", "tiff", "tif");
	if (preg_match_all($reg_exUrl, $string, $url)) {

		// Loop through all matches
		foreach ($url[0] as $newLinks) {
			if (strstr($newLinks, ":") === false) {
				$link = 'http://' . $newLinks;
			} else {
				$link = $newLinks;
			}

			$search = $newLinks;
			$temp = explode(".", $newLinks );

			$extension = end($temp);

			if ( in_array($extension, $imgExts) ){
				$replace = '<img src= "' . $link . '"  style = "max-width:100%">';

			} else {
				$replace = '<a href="' . $link . '" title="' . $newLinks . '" target="_blank">' . $link . '</a>';
			}

			$string = str_replace($search, $replace, $string);
		}
	}

	return $string;
}
